    private void btnGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGuardarActionPerformed
        // TODO add your handling code here:

// TODO add your handling code here:System.out.println(cblSeleccionSeguro.getSelectedItem().toString());
        if (cblSeleccionSeguro.getSelectedItem().toString().equals("Vida")) {
            System.out.println("Se guardara Seguro Vida");

            Vida v = new Vida();
            v.setIdentificador(Integer.parseInt(txtIdentificador.getText()));
            v.setNombreCliente(txtNombreCliente.getText());
            v.setCosto(Integer.parseInt(txtCosto.getText()));
            v.setFechaVigencia(Integer.parseInt(txtFechaVigencia.getText()));
            v.setNombreAsegurado(txtNombreAsegurado.getText());
            v.setEdadAsegurado(Integer.parseInt(txtEdadAsegurado.getText()));
            v.setValorFallecimiento(Integer.parseInt(txtValorFallecimiento.getText()));

            boolean resultado = false;
            resultado = ControlVida.crearVida(v);

            if (resultado) {
                txtIdentificador.setText("");
                txtNombreCliente.setText("");
                txtCosto.setText("");
                txtFechaVigencia.setText("");
                txtNombreAsegurado.setText("");
                txtEdadAsegurado.setText("");
                txtValorFallecimiento.setText("");

            } else {
                txtIdentificador.setText("Error");
                txtNombreCliente.setText("Error");
                txtCosto.setText("Error");
                txtFechaVigencia.setText("Error");
                txtNombreAsegurado.setText("Error");
                txtEdadAsegurado.setText("Error");
                txtValorFallecimiento.setText("Error");
            }

        } else {
        }
        {
            if (cblSeleccionSeguro.getSelectedItem().toString().equals("Automotriz")) {
                System.out.println("Se guardara Seguro Automotriz");

                Automotriz a = new Automotriz();
                ///Vehiculo vehic = new Vehiculo();
                a.setIdentificador(Integer.parseInt(txtIdentificador.getText()));
                a.setNombreCliente(txtNombreCliente.getText());
                a.setCosto(Integer.parseInt(txtCosto.getText()));
                a.setFechaVigencia(Integer.parseInt(txtFechaVigencia.getText()));
                a.setDeducibleUf(Integer.parseInt(txtDeducible.getText()));
                a.vehiculo.setPatente(txtPatente.getText());
                a.vehiculo.setMarca(txtMarca.getText());
                a.vehiculo.setAvaluoFiscal(Integer.parseInt(txtAvaluoFiscal.getText()));

                boolean resultado = false;
                resultado = ControlAutomotriz.crearAutomotriz(a);

                if (resultado) {
                    txtIdentificador.setText("");
                    txtNombreCliente.setText("");
                    txtCosto.setText("");
                    txtFechaVigencia.setText("");
                    txtDeducible.setText("");
                    txtPatente.setText("");
                    txtMarca.setText("");
                    txtAvaluoFiscal.setText("");

                } else {
                    txtIdentificador.setText("Error");
                    txtNombreCliente.setText("Error");;
                    txtCosto.setText("Error");;
                    txtFechaVigencia.setText("Error");;
                    txtDeducible.setText("Error");;
                    txtPatente.setText("Error");;
                    txtMarca.setText("Error");;
                    txtAvaluoFiscal.setText("Error");;
                }

            } else {
                if (cblSeleccionSeguro.getSelectedItem().toString().equals("Seleccionar")) {
                    System.out.println("Haga la selección de tipo seguro");

                } else {
                }
            }

            // TODO add your handling code here:
        }

    }//GEN-LAST:event_btnGuardarActionPerformed

