    private void cblSeleccionSeguroActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cblSeleccionSeguroActionPerformed

// TODO add your handling code here:System.out.println(cblSeleccionSeguro.getSelectedItem().toString());
        if (cblSeleccionSeguro.getSelectedItem().toString().equals("Vida")) {
            System.out.println("Selecciono VIDA");
            txtDeducible.setEnabled(false);
            txtPatente.setEnabled(false);
            txtMarca.setEnabled(false);
            txtAvaluoFiscal.setEnabled(false);
            txtNombreAsegurado.setEnabled(true);
            txtEdadAsegurado.setEnabled(true);
            txtValorFallecimiento.setEnabled(true);

        } else {
        }
        {
            if (cblSeleccionSeguro.getSelectedItem().toString().equals("Automotriz")) {
                System.out.println("Selecciono Automotriz");
                txtPatente.setEnabled(true);
                txtDeducible.setEnabled(true);
                txtMarca.setEnabled(true);
                txtAvaluoFiscal.setEnabled(true);
                txtNombreAsegurado.setEnabled(false);
                txtEdadAsegurado.setEnabled(false);
                txtValorFallecimiento.setEnabled(false);

            } else {
                if (cblSeleccionSeguro.getSelectedItem().toString().equals("Seleccionar")) {
                    System.out.println("Sin Seleccionar");
                    txtPatente.setEnabled(false);
                    txtDeducible.setEnabled(false);
                    txtMarca.setEnabled(false);
                    txtAvaluoFiscal.setEnabled(false);
                    txtNombreAsegurado.setEnabled(false);
                    txtEdadAsegurado.setEnabled(false);
                    txtValorFallecimiento.setEnabled(false);
                } else {
                }
            }

            // TODO add your handling code here:
        }

    }//GEN-LAST:event_cblSeleccionSeguroActionPerformed

